
 public class FirstTypeFunction1 {
	static void pattern(char ch,int num) {
		for(int i=1;i<=num;i++) {
			System.out.println(ch);
	}
	System.out.println("**");
	System.out.println("..... ");
	}

		public static void main(String[] args) {
		// TODO Auto-generated method stub
           pattern('*',5);
			System.out.println("hello");
			pattern(',',9);
			System.out.println("sahasra");
			pattern('.',5);
			System.out.println("welcome to newzland");

		}

		}
